function MODULE:VC_canAfford(ply, amount)
    if ply:getChar():hasMoney(amount) then return true end

    return false
end

---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MODULE:VC_canAddMoney(ply, amount)
    ply:getChar():giveMoney(amount)

    return false
end

---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MODULE:VC_canRemoveMoney(ply, amount)
    ply:getChar():takeMoney(amount)

    return false
end
