function Core:OnReloaded()
    if not ModulesLoaded then
        lia.module.initialize()
        ModulesLoaded = true
    end

    if CLIENT then hook.Run("LoadLiliaFonts", lia.config.Font, lia.config.GenericFont) end
    lia.faction.formatModelData()
end

