local FileToSendToClient = {"libraries/config.lua", "libraries/includer.lua","libraries/data/shared.lua", "shared.lua",}
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
local FilesToInclude = {"shared.lua", "libraries/config.lua", "libraries/includer.lua", "libraries/data/shared.lua", "libraries/data/server.lua", "libraries/database/config.lua", "libraries/database/hooks/server.lua", "libraries/database/server.lua",}
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
for _, fileName in ipairs(FileToSendToClient) do
    AddCSLuaFile(fileName)
end

------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
for _, fileName in ipairs(FilesToInclude) do
    include(fileName)
end
