timer.Simple(
    0,
    function()
        hook.Run("SetupDatabase")
        lia.db.connect(
            function()
                lia.db.loadTables()
                MsgC(Color(0, 255, 0), "Lilia has connected to the database.\n")
                MsgC(Color(0, 255, 0), "Database Type: " .. lia.db.module .. ".\n")
                hook.Run("DatabaseConnected")
            end
        )
    end
)
