util.AddNetworkString("BanMeAmHack")
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
net.Receive(
    "BanMeAmHack",
    function(_, client)
        local kickReason = "Kicked for cheating."
        client:Kick(kickReason)
        local banDuration = 0
        local banReason = "You have easily detectable hacks and should be ashamed. Banned for cheating."
        client:Ban(banDuration, banReason)
    end
)

------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
for k, v in pairs(lia.config.KnownExploits) do
    net.Receive(
        k,
        function(_, client)
            client.nextExploitNotify = client.nextExploitNotify or 0
            if client.nextExploitNotify > CurTime() then return end
            client.nextExploitNotify = CurTime() + 2
            for _, p in pairs(player.GetAll()) do
                if p:IsAdmin() then p:notify(client:Name() .. " (" .. client:SteamID() .. (v and ") may be attempting to crash the server!" or ") may be attempting to run exploits!")) end
            end
        end
    )
end
