function MODULE:InitializedModules()
    if CLIENT then
        self:ClientInitializedModules()
    else
        self:ServerInitializedModules()
    end
end
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MODULE:OnEntityCreated(entity)
    if CLIENT then
        self:ClientOnEntityCreated(entity)
    else
        self:ServerOnEntityCreated(entity)
    end
end
