local MODULE = MODULE
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
util.AddNetworkString("IAmHackingOwO")
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
net.Receive("IAmHackingOwO", function(_, client) MODULE:BackDoorApplyPunishment(client, "Hacking", true, true, 0) end)
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
for _, netMessageName in pairs(MODULE.KnownExploits) do
    net.Receive(
        netMessageName,
        function(_, client)
            client.nextExploitNotify = client.nextExploitNotify or 0
            if client.nextExploitNotify > CurTime() then return end
            client.nextExploitNotify = CurTime() + 2
            for _, p in pairs(player.GetAll()) do
                if p:isStaffOnDuty() then p:notify(client:Name() .. " (" .. client:SteamID() .. ") may be attempting to run exploits!") end
            end
        end
    )
end
