local MODULE = MODULE
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MODULE:InitializedModules()
    if MODULE.GamemodeWorkshop then
        for i = 1, #MODULE.GamemodeWorkshop do
            resource.AddWorkshop(MODULE.GamemodeWorkshop[i])
        end
    end

    if MODULE.AutoWorkshopDownloader and engine.GetAddons() then
        for i = 1, #engine.GetAddons() do
            resource.AddWorkshop(engine.GetAddons()[i].wsid)
        end
    end
end
