function MODULE:HUDPaint()
    if hook.Run("ShouldDrawCrosshair") then hook.Run("DrawCrosshair", weapon) end
    if hook.Run("ShouldDrawVignette", weapon) then hook.Run("DrawAmmo", weapon) end
    if hook.Run("ShouldDrawAmmo", weapon) then hook.Run("DrawAmmo", weapon) end
end

